import React from 'react';
import TodoList from './TodoList.js';


export default class TodoApp extends React.Component{
    constructor(props){
        super(props);

        this.state = {
            items: [],
            text: ''
        };

        this.handleClick = this.handleClick.bind(this);
        this.handleChange = this.handleChange.bind(this);
    }

    handleClick(e){
        if (!this.state.text.length) {
            return;
        }
        
        const newItem = {
            id: Date.now(),
            name: this.state.text
        }

        this.setState({
            items: this.state.items.concat(newItem),
            text: ''
        })
    }

    handleChange(e){
        this.setState({
            text: e.target.value
        });
    }

    render(){
        return (
            <main className='jumbotron d-flex flex-column text-center'>
                <h3 className='display-4'>Todo List</h3>
                {/* <TodoList items={this.state.items} /> */}
                <div className="input-group w-25 m-3 align-self-center">
                    <div className="input-group-prepend">
                        <button className="btn btn-outline-success" type="button" id="button-addon1" onClick={this.handleClick}>Add item</button>
                    </div>
                    
                    <input type="text" className="form-control" placeholder="" onChange={this.handleChange} value={this.state.text} />
                </div>
            </main>
        )
    }
}
