import React from 'react';
import MakeList from './MakeList.js';

export default class App extends React.Component{
    constructor(props){
        super(props);

        this.state = {isListActive: true};
        this.handleClick = this.handleClick.bind(this);
    }

    render(){
        const message = this.state.isListActive && <h3 className='blo'>List is active</h3>;
        const list = [1,2,3,4];
        return (
            <main>
                <MakeList list = {list} onClick={this.handleClick} />
                {message}   
            </main>
            
        );
    }

    handleClick(e){
        this.setState({
            isListActive: !this.state.isListActive
        });
        console.log(e.target);
        
    }
}
