import React from 'react';

export default class App extends React.Component{
    constructor(props){
        super(props);
        this.state = {isMessageActive: true};
        this.handleClick = this.handleClick.bind(this)
    }
    handleClick(e){
        this.setState({
            isMessageActive: !this.state.isMessageActive
        });
    }
    render(){
        return (
            <main>
                <button onClick={this.handleClick}>{this.isMessageActive ? 'Hide message' : 'Show message'}</button>
                <h1>{this.props.message}, World!</h1>
            </main>
        )
    }
}
