import React from 'react';
import MakeList from './MakeList.js';

export default class App extends React.Component{
    constructor(props){
        super(props);
        this.state = {isMessageActive: true};
        this.handleClick = this.handleClick.bind(this);
    }
    handleClick(e){
        this.setState({
            isMessageActive: !this.state.isMessageActive
        });
    }
    render(){
        const message = this.state.isMessageActive && <h1 className='display-3'>{this.props.message}, World!</h1>;
        return (
            <main className='container text-center'>
                <button onClick={this.handleClick} title='Click me!'>{this.state.isMessageActive ? 'Hide message' : 'Show message'}</button>
                <MakeList list={['Learn React', '...', 'Profit!']} className='jumbotron'/>
                {message}
            </main>
        )
    }
}
